'use client';

import { motion, useMotionTemplate, useMotionValue } from 'framer-motion';
import Image from 'next/image';
import Link from 'next/link';
import { Heading } from '@/components/heading';
import { Text } from '@/components/text';

const IntegrationSection = () => {
  const mouseX = useMotionValue(0);
  const mouseY = useMotionValue(0);

  return (
    <section className="relative pt-12 pb-48">
      <div className="space-y-16">
        <div className="max-w-full text-center space-y-4">
          <Heading size="8" weight="medium" className="text-white/80">
            Integrate with any service
          </Heading>
          <div className="sm:px-20 md:max-w-3xl md:mx-auto text-balance">
            <Text size="4">
              Convert your React code into HTML or Plain Text and send it with any email service provider.
            </Text>
          </div>
        </div>
        <div className="grid grid-cols-4 gap-8">
          {items.map((item, index) => (
            <Link
              key={index}
              onMouseMove={(e) => {
                const { left, top } = e.currentTarget.getBoundingClientRect();

                mouseX.set(e.clientX - left);
                mouseY.set(e.clientY - top);
              }}
              className="group relative rounded-3xl bg-slate-4 p-px"
              href={item.href}
            >
              <motion.div
                className="-inset-px pointer-events-none absolute rounded-3xl opacity-0 transition duration-300 group-hover:opacity-20 [--color:rgb(187,243,254,0.6)]"
                style={{
                  background: useMotionTemplate`radial-gradient(130px circle at ${mouseX}px ${mouseY}px, var(--color), transparent 80%)`,
                }}
              />
              <div className="relative z-[5] h-32 flex items-center justify-center py-4 px-24 rounded-3xl bg-black overflow-hidden">
                <div className="relative mx-auto block w-fit">{item.logo}</div>
                <Line />
                <Blur />
              </div>
            </Link>
          ))}
        </div>
      </div>
      <Image
        alt=""
        className="pointer-events-none absolute inset-0 -top-40 z-[3] select-none mix-blend-lighten opacity-60"
        fill
        priority
        src="/static/bg.png"
      />
    </section>
  );
};

const Blur = () => {
  return (
    <div
      aria-hidden="true"
      className="pointer-events-none absolute z-[1] -top-1 left-1/2 h-[200px] w-full max-w-[200px] -translate-x-1/2 -translate-y-1/2 md:max-w-[500px]"
      style={{
        background:
          'conic-gradient(from 90deg at 50% 50%, #00000000 50%, #0a0a0a 50%),radial-gradient(rgba(37, 99, 235, 0.1) 0%, transparent 80%)',
      }}
    />
  );
};

const Line = () => {
  return (
    <div
      aria-hidden
      className="absolute top-0 right-4 h-px w-32 bg-gradient-to-l from-transparent via-cyan-12/30 via-50% to-transparent"
    />
  );
};

